            name='recovery',
            from_state='I',
            to_state='R',
            rate='gamma')
        .add_edge_interaction(
            name='infection',
            from_state='S',
            to_state='I',
            inducer='I',
            network_layer='contact_network_layer',
            rate='beta'))
